mapper.select:
SELECT 
FROM reply


		AND topic_id = #{topic_id}



		AND reply_content like #{content}


		AND user_id = #{user_id}


		AND reply_user_id = #{reply_user_id}


		AND reply_status = #{reply_status}

SELECT count(reply_id) FROM reply


		AND reply_status = #{reply_status}


		AND reply_user_id = #{reply_user_id}


		AND user_id = #{user_id}


		AND topic_id = #{topic_id}


		AND prop1 = #{prop1}


		AND prop2 = #{prop2}


		AND prop3 = #{prop3}


		AND prop4 = #{prop4}


		AND prop5 = #{prop5}


		AND prop6 = #{prop6}


		AND prop7 = #{prop7}


		AND prop8 = #{prop8}


		AND prop9 = #{prop9}


		AND prop10 = #{prop10}

SELECT u.user_id AS user_id, u.user_name AS user_name, CASE u.user_sex WHEN 0 THEN '' WHEN 1 THEN '男' WHEN 2 THEN '女' END AS user_sex, u.user_ico_url AS user_ico_url, date_format(r.reply_time,'%Y-%m-%d %k:%m:%s') AS publish_time,
(SELECT count(reply_id) + 1 FROM reply WHERE reply.topic_id = r.topic_id AND reply.reply_time < r.reply_time) AS reply_floor , r.reply_content AS publish_content, 1 AS sign, u.user_introduction AS user_introduction FROM reply AS r INNER JOIN user AS u ON r.user_id = u.user_id
AND r.topic_id = #{topic_id}
AND r.reply_user_id = #{reply_user_id}
AND r.relpy_status = #{reply_status}
ORDER BY r.reply_time ASC
LIMIT #{start}, #{size}
